import { defineConfig, loadEnv } from 'vite'
import react from '@vitejs/plugin-react'
import fs from 'node:fs'

// https://vitejs.dev/config/
export default defineConfig(({ mode }) => {
  const env = loadEnv(mode, process.cwd(), '')
  const authProxyUrl = env.VITE_AUTH_PROXY_URL || 'http://localhost:8080'
  const envOutDir = env.VITE_OUT_DIR

  const chooseOutDir = () => {
    if (envOutDir) return envOutDir
    try {
      // If dist exists but is not writable (e.g. generated by root in docker), fall back.
      fs.mkdirSync('dist', { recursive: true })
      fs.accessSync('dist', fs.constants.W_OK)
      return 'dist'
    } catch {
      return 'dist-local'
    }
  }

  return {
    plugins: [react()],
    server: {
      host: '0.0.0.0', // Доступ извне контейнера
      port: 3000,
      proxy: {
        // Experiment-service API and Telemetry ingest (through auth-proxy)
        '/api': {
          target: authProxyUrl,
          changeOrigin: true,
        },
        // Auth-service projects API (through auth-proxy)
        '/projects': {
          target: authProxyUrl,
          changeOrigin: true,
        },
      },
    },
    build: {
      outDir: chooseOutDir(),
    },
    test: {
      globals: true,
      environment: 'jsdom',
      setupFiles: './src/setupTests.ts',
      css: true,
    },
  }
})

